import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { NavLink } from 'react-router-dom';
import { logout } from '../../redux/actions/userActions';

// UI imports and objects
import { ReactComponent as NavAvatar } from '../../assets/navbar_avatar_.svg';
import { ReactComponent as Ticket } from '../../assets/navbar-ticket.svg';
import {
	AiOutlineDashboard,
	AiOutlineTeam,
	AiOutlineUser,
	AiOutlinePoweroff
} from 'react-icons/ai';
// import { HiOutlineTicket, HiOutlineClipboardList } from 'react-icons/hi';
import { HiOutlineTicket } from 'react-icons/hi';
import { IoCalendarOutline } from 'react-icons/io5';
import {
	Stack,
	Heading,
	Text,
	Avatar,
	Icon,
	Link,
	Button,
	HStack
} from '@chakra-ui/react';

const navLinkStyle = {
	activeStyle : {
		color       : '#4299E1',
		borderRight : '3px solid #4299E1'
	},
	textDecor   : 'none',
	_hover      : {
		textDecor : 'none',
		color     : '#4299E1'
	},
	height      : '3rem',
	color       : 'gray.500'
};

const buttonStyle = {
	textDecor : 'none',
	_hover    : {
		textDecor : 'none',
		color     : '#4299E1'
	},
	height    : '3rem',
	color     : 'gray.500',
	fontSize  : '1.2rem'
};

const iconStyle = {
	fontSize : '2rem',
	ml       : '3.5rem',
	mr       : '0.7rem'
};

const NavBar = () => {
	const userLogin = useSelector((state) => state.userLogin);
	const { userInfo } = userLogin;

	const dispatch = useDispatch();

	return (
		<Stack direction="column" py="1rem" height="100%" justifyContent="space-between">
			<Stack direction="column" alignItems="center" spacing={0.75}>
				<Heading fontSize="1.6rem" color="blue.400" mb="1rem">
					<Icon as={Ticket} mr="0.5rem" />
					<Text as="span">Ticket Service</Text>
				</Heading>
				<Avatar as={NavAvatar} size="xl" />
				<Text color="blue.400" fontSize="2xl" fontWeight="bold">
					{userInfo && userInfo.firstname}
				</Text>
				<Text color="gray.600" fontSize="sm">
					{userInfo && userInfo.isAdmin ? 'Administrator' : 'Student Worker'}
				</Text>
			</Stack>

			<Stack direction="column" textAlign="center" fontSize="1.2rem">
				<Link as={NavLink} to="/dashboard" end {...navLinkStyle}>
					<HStack h="100%">
						<Icon as={AiOutlineDashboard} {...iconStyle} />
						<Text as="span">Dashboard</Text>
					</HStack>
				</Link>
				<Link as={NavLink} to="/dashboard/tickets" {...navLinkStyle}>
					<HStack h="100%">
						<Icon as={HiOutlineTicket} {...iconStyle} />
						<Text as="span">Tickets</Text>
					</HStack>
				</Link>
				<Link as={NavLink} to="/dashboard/events" {...navLinkStyle}>
					<HStack h="100%">
						<Icon as={IoCalendarOutline} {...iconStyle} />
						<Text as="span">Events</Text>
					</HStack>
				</Link>
				<Link as={NavLink} to="/dashboard/customers" {...navLinkStyle}>
					<HStack h="100%">
						<Icon as={AiOutlineUser} {...iconStyle} />
						<Text as="span">Customers</Text>
					</HStack>
				</Link>
				{userInfo &&
				userInfo.isAdmin && (
					<Link as={NavLink} to="/dashboard/team" {...navLinkStyle}>
						<HStack h="100%">
							<Icon as={AiOutlineTeam} {...iconStyle} />
							<Text as="span">Team</Text>
						</HStack>
					</Link>
				)}
				{/* <Link as={NavLink} to="/dashboard/waitlists" {...navLinkStyle}>
					<HStack h="100%">
						<Icon as={HiOutlineClipboardList} {...iconStyle} />
						<Text as="span">Waitlists</Text>
					</HStack>
				</Link> */}
			</Stack>
			<Stack direction="column" justifyContent="center" fontSize="1.2rem">
				<Button variant="link" {...buttonStyle} onClick={() => dispatch(logout())}>
					<HStack h="100%" w="100%">
						<Icon as={AiOutlinePoweroff} {...iconStyle} />
						<Text as="span" fontWeight="normal">
							Log Out
						</Text>
					</HStack>
				</Button>
			</Stack>
		</Stack>
	);
};

export default NavBar;
